$(document).ready(function(){
  $("#menuBtn").click(() => {
        $(".menu-content").toggle();
        $(".menu-icon").toggleClass("fa-bars fa-xmark");
    });

    $(window).resize(function () {
        if ($(window).width() >= 600) {
            $(".menu-content").css({
                display: "flex",
                "min-width": "50%",
                position: "relative",
                "background-color": "transparent"
            });
        } else {
            $(".menu-content").css({
                "min-width": "100%",
                height: "auto",
                "background-color": "#1a1a1a",
                position: "absolute",
                top: "100%",
                left: "0",
                padding: "8px",
                display: "none"
            });
            $('.menu-icon').removeClass('fa-xmark');
            $('.menu-icon').addClass('fa-bars');
        }
    });
});